import { React, useState, useEffect } from "react";

export default function Search({setIsSearch, furnitures, setFurnitures}) {
    const [input, setInput] = useState('');

    useEffect(() => {
        const storedState = history.state;
        if (storedState && storedState.furnitures) {
          setItems(storedState.items);
          setQuery(storedState.input);
        }
      }, []);

    useEffect(() => {
        const state = { furnitures, input };
        history.replaceState(state, '', '');
    }, [furnitures, query]);

    function handleInput(e) {
        setInput(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();
        setIsSearch(false);
        const searchResult = furnitures.filter(furniture => {
            console.log(furniture.name.toLowerCase().replace(/\s/g, ''))
            return furniture.name.toLowerCase().replace(/\s/g, '').includes(input.toLowerCase().replace(/\s/g, '')) || 
                furniture.designer.toLowerCase().replace(/\s/g, '').includes(input.toLowerCase().replace(/\s/g, ''));
        })
        console.log(searchResult);
        setFurnitures(searchResult);
        setInput('');
    }


    return (
        <div className='search-container'>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Type here" autoComplete="off" autoFocus onChange={handleInput} value={input} />
            </form>

            <i className='bx bx-x' onClick={() => setIsSearch(false)}></i>
        </div>
    )
}
