import { React, useState} from "react";
import { NavLink } from "react-router-dom";

export default function Header() {
    const [isDropDownMenu, setIsDropDownMenu] = useState(false);

    function hideDropDown() {
        setIsDropDownMenu(false)
    }

    return (
        <div className='header-container'>
            <div className='nav-container flex-box'>
                <h1>HAUS</h1>

                <div className={`dropdown-menu-container flex-box ${isDropDownMenu ? 'show-dropdown' : ''}`}>
                    <ul className='dropdown-menu flex-box'>
                        <li><NavLink to='/' exact='true'>Furnitures</NavLink></li>
                        <li><NavLink to='/' exact='true'>Cart(1)</NavLink></li>
                        <li><NavLink to='/' exact='true'>Profile</NavLink></li>
                        <li className='dropdown-menu-logout flex-box'> 
                            <p>Logout</p> 
                            <i className='bx bx-log-out' ></i> 
                        </li>
                    </ul>

                    <i className='bx bx-x' onClick={() => setIsDropDownMenu(false)}></i>
                </div>

                <i className='bx bx-menu-alt-right' style={{color: '#000'}} onClick={() => setIsDropDownMenu(true)}></i>
            </div>
        </div>
    )
}
