import { React, useEffect, useState } from "react";
import { useLocatio, useParams } from "react-router-dom";

export default function Inspect() {
    const [furniture, setFurniture] = useState({});
    const { id } = useParams();

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch(`https://haus-db.onrender.com/furnitures/${id}`);
            const data = await response.json();
            setFurniture(data);
          };
          fetchData();
      },[])

      return ( 
        <div className='inspect-container'>
            <div className="inspect-info-container">
                <div className="inspect-item-container">
                    div.inspec
                   <p>{furniture.name}</p>
                   <p>USD {furniture.price && furniture.price.toLocaleString()}</p>
                </div>
            </div>
        </div>
      )


}
